// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IDiamondLoupe } from "contracts/interfaces/IDiamondLoupe.sol";

import { LibStorage as s } from "../libraries/LibStorage.sol";

import "../Storage.sol";

/// @title DiamondLoupe
/// @author Cooper Labs
/// @custom:contact security@cooperlabs.xyz
/// @dev Reference: EIP-2535 Diamonds
/// @dev Forked from https://github.com/mudgen/diamond-3/blob/master/contracts/facets/DiamondLoupeFacet.sol by mudgen
contract DiamondLoupe is IDiamondLoupe {
  /// @inheritdoc IDiamondLoupe
  function facets() external view override returns (Facet[] memory facets_) {
    DiamondStorage storage ds = s.diamondStorage();
    uint256 selectorCount = ds.selectors.length;
    facets_ = new Facet[](selectorCount);
    uint16[] memory numFacetSelectors = new uint16[](selectorCount);
    uint256 numFacets;

    for (uint256 selectorIndex; selectorIndex < selectorCount; selectorIndex++) {
      bytes4 selector = ds.selectors[selectorIndex];
      address facetAddress_ = ds.selectorInfo[selector].facetAddress;
      bool continueLoop = false;
      // Find the functionSelectors array for selector and add selector to it
      for (uint256 facetIndex; facetIndex < numFacets; facetIndex++) {
        if (facets_[facetIndex].facetAddress == facetAddress_) {
          facets_[facetIndex].functionSelectors[numFacetSelectors[facetIndex]] = selector;
          numFacetSelectors[facetIndex]++;
          continueLoop = true;
          break;
        }
      }
      // If functionSelectors array exists for selector then continue loop
      if (continueLoop) {
        continueLoop = false;
        continue;
      }

      // Create a new functionSelectors array for selector
      facets_[numFacets].facetAddress = facetAddress_;
      facets_[numFacets].functionSelectors = new bytes4[](selectorCount);
      facets_[numFacets].functionSelectors[0] = selector;
      numFacetSelectors[numFacets] = 1;
      numFacets++;
    }
    for (uint256 facetIndex; facetIndex < numFacets; facetIndex++) {
      uint256 numSelectors = numFacetSelectors[facetIndex];
      bytes4[] memory selectors = facets_[facetIndex].functionSelectors;
      // Set the number of selectors
      assembly ("memory-safe") {
        mstore(selectors, numSelectors)
      }
    }
    // Set the number of facets
    assembly ("memory-safe") {
      mstore(facets_, numFacets)
    }
  }

  /// @inheritdoc IDiamondLoupe
  function facetFunctionSelectors(address _facet)
    external
    view
    override
    returns (bytes4[] memory _facetFunctionSelectors)
  {
    DiamondStorage storage ds = s.diamondStorage();
    uint256 selectorCount = ds.selectors.length;
    uint256 numSelectors;
    _facetFunctionSelectors = new bytes4[](selectorCount);

    for (uint256 selectorIndex; selectorIndex < selectorCount; selectorIndex++) {
      bytes4 selector = ds.selectors[selectorIndex];
      address facetAddress_ = ds.selectorInfo[selector].facetAddress;
      if (_facet == facetAddress_) {
        _facetFunctionSelectors[numSelectors] = selector;
        numSelectors++;
      }
    }
    // Set the number of selectors in the array
    assembly ("memory-safe") {
      mstore(_facetFunctionSelectors, numSelectors)
    }
  }

  /// @inheritdoc IDiamondLoupe
  function facetAddresses() external view override returns (address[] memory facetAddresses_) {
    DiamondStorage storage ds = s.diamondStorage();
    uint256 selectorCount = ds.selectors.length;
    // Create an array set to the maximum size possible
    facetAddresses_ = new address[](selectorCount);
    uint256 numFacets;
    // Loop through function selectors
    for (uint256 selectorIndex; selectorIndex < selectorCount; selectorIndex++) {
      bytes4 selector = ds.selectors[selectorIndex];
      address facetAddress_ = ds.selectorInfo[selector].facetAddress;
      bool continueLoop = false;
      // See if we have collected the address already and break out of loop if we have
      for (uint256 facetIndex; facetIndex < numFacets; facetIndex++) {
        if (facetAddress_ == facetAddresses_[facetIndex]) {
          continueLoop = true;
          break;
        }
      }
      // Continue loop if we already have the address
      if (continueLoop) {
        continueLoop = false;
        continue;
      }
      // Include address
      facetAddresses_[numFacets] = facetAddress_;
      numFacets++;
    }
    // Set the number of facet addresses in the array
    assembly ("memory-safe") {
      mstore(facetAddresses_, numFacets)
    }
  }

  /// @inheritdoc IDiamondLoupe
  function facetAddress(bytes4 _functionSelector) external view override returns (address facetAddress_) {
    DiamondStorage storage ds = s.diamondStorage();
    facetAddress_ = ds.selectorInfo[_functionSelector].facetAddress;
  }
}

contracts/parallelizer/facets/Getters.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

import { IGetters } from "contracts/interfaces/IGetters.sol";

import { LibOracle } from "../libraries/LibOracle.sol";
import { LibGetters } from "../libraries/LibGetters.sol";
import { LibStorage as s } from "../libraries/LibStorage.sol";
import { LibWhitelist } from "../libraries/LibWhitelist.sol";

import "../../utils/Constants.sol";
import "../../utils/Errors.sol";
import "../Storage.sol";

/// @title Getters
/// @author Cooper Labs
/// @custom:contact security@cooperlabs.xyz
/// @dev There may be duplicates in the info provided by the getters defined here
/// @dev This contract is an authorized fork of Angle's `Getters` contract
/// https://github.com/AngleProtocol/angle-transmuter/blob/main/contracts/parallelizer/facets/Getters.sol
contract Getters is IGetters {
  /// @inheritdoc IGetters
  function isValidSelector(bytes4 selector) external view returns (bool) {
    return s.diamondStorage().selectorInfo[selector].facetAddress != address(0);
  }

  /// @inheritdoc IGetters
  function tokenP() external view returns (ITokenP) {
    return s.transmuterStorage().tokenP;
  }

  /// @inheritdoc IGetters
  function getCollateralList() external view returns (address[] memory) {
    return s.transmuterStorage().collateralList;
  }

  /// @inheritdoc IGetters
  function getCollateralInfo(address collateral) external view returns (Collateral memory) {
    return s.transmuterStorage().collaterals[collateral];
  }

  /// @inheritdoc IGetters
  function getCollateralDecimals(address collateral) external view returns (uint8) {
    return s.transmuterStorage().collaterals[collateral].decimals;
  }

  /// @inheritdoc IGetters
  function getCollateralMintFees(address collateral)
    external
    view
    returns (uint64[] memory xFeeMint, int64[] memory yFeeMint)
  {
    Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
    return (collatInfo.xFeeMint, collatInfo.yFeeMint);
  }

  /// @inheritdoc IGetters
  function getCollateralBurnFees(address collateral)
    external
    view
    returns (uint64[] memory xFeeBurn, int64[] memory yFeeBurn)
  {
    Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
    return (collatInfo.xFeeBurn, collatInfo.yFeeBurn);
  }

  /// @inheritdoc IGetters
  function getRedemptionFees()
    external
    view
    returns (uint64[] memory xRedemptionCurve, int64[] memory yRedemptionCurve)
  {
    ParallelizerStorage storage ts = s.transmuterStorage();
    return (ts.xRedemptionCurve, ts.yRedemptionCurve);
  }

  /// @inheritdoc IGetters
  /// @dev This function may revert and overflow if the collateral ratio is too big due to a too small
  /// amount of `stablecoinsIssued`. Due to this, it is recommended to initialize the system with a non
  /// negligible amount of `stablecoinsIssued` so DoS attacks on redemptions which use this function
  /// become economically impossible
  function getCollateralRatio() external view returns (uint64 collatRatio, uint256 stablecoinsIssued) {
    ParallelizerStorage storage ts = s.transmuterStorage();
    // Reentrant protection
    if (ts.statusReentrant == ENTERED) revert ReentrantCall();

    (collatRatio, stablecoinsIssued,,,) = LibGetters.getCollateralRatio();
  }

  /// @inheritdoc IGetters
  function getIssuedByCollateral(address collateral)
    external
    view
    returns (uint256 stablecoinsFromCollateral, uint256 stablecoinsIssued)
  {
    ParallelizerStorage storage ts = s.transmuterStorage();
    uint256 _normalizer = ts.normalizer;
    return (
      (uint256(ts.collaterals[collateral].normalizedStables) * _normalizer) / BASE_27,
      (uint256(ts.normalizedStables) * _normalizer) / BASE_27
    );
  }

  /// @inheritdoc IGetters
  function getTotalIssued() external view returns (uint256) {
    ParallelizerStorage storage ts = s.transmuterStorage();
    return (uint256(ts.normalizedStables) * uint256(ts.normalizer)) / BASE_27;
  }

  /// @inheritdoc IGetters
  function getManagerData(address collateral) external view returns (bool, IERC20[] memory, bytes memory) {
    Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
    if (collatInfo.isManaged > 0) {
      return (true, collatInfo.managerData.subCollaterals, collatInfo.managerData.config);
    }
    return (false, new IERC20[](0), "");
  }

  /// @inheritdoc IGetters
  /// @dev This function is not optimized for gas consumption as for instance the `burn` value for collateral
  /// is computed twice: once in `readBurn` and once in `getBurnOracle`
  function getOracleValues(address collateral)
    external
    view
    returns (uint256 mint, uint256 burn, uint256 ratio, uint256 minRatio, uint256 redemption)
  {
    bytes memory oracleConfig = s.transmuterStorage().collaterals[collateral].oracleConfig;
    (burn, ratio) = LibOracle.readBurn(oracleConfig);
    (minRatio,) = LibOracle.getBurnOracle(collateral, oracleConfig);
    return (LibOracle.readMint(oracleConfig), burn, ratio, minRatio, LibOracle.readRedemption(oracleConfig));
  }

  /// @inheritdoc IGetters
  function getOracle(address collateral)
    external
    view
    returns (
      OracleReadType oracleType,
      OracleReadType targetType,
      bytes memory oracleData,
      bytes memory targetData,
      bytes memory hyperparameters
    )
  {
    return LibOracle.getOracle(collateral);
  }

  /// @inheritdoc IGetters
  function isPaused(address collateral, ActionType action) external view returns (bool) {
    if (action == ActionType.Mint || action == ActionType.Burn) {
      Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
      if (collatInfo.decimals == 0) revert NotCollateral();
      if (action == ActionType.Mint) {
        return collatInfo.isMintLive == 0;
      } else {
        return collatInfo.isBurnLive == 0;
      }
    } else {
      return s.transmuterStorage().isRedemptionLive == 0;
    }
  }

  /// @inheritdoc IGetters
  function isTrusted(address sender) external view returns (bool) {
    return s.transmuterStorage().isTrusted[sender] == 1;
  }

  /// @inheritdoc IGetters
  function isTrustedSeller(address sender) external view returns (bool) {
    return s.transmuterStorage().isSellerTrusted[sender] == 1;
  }

  /// @inheritdoc IGetters
  function isWhitelistedForType(WhitelistType whitelistType, address sender) external view returns (bool) {
    return s.transmuterStorage().isWhitelistedForType[whitelistType][sender] > 0;
  }

  /// @inheritdoc IGetters
  /// @dev This function is non view as it may consult external non view functions from whitelist providers
  function isWhitelistedForCollateral(address collateral, address sender) external returns (bool) {
    Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
    return (collatInfo.onlyWhitelisted == 0 || LibWhitelist.checkWhitelist(collatInfo.whitelistData, sender));
  }

  /// @inheritdoc IGetters
  function isWhitelistedCollateral(address collateral) external view returns (bool) {
    return s.transmuterStorage().collaterals[collateral].onlyWhitelisted == 1;
  }

  /// @inheritdoc IGetters
  function getCollateralWhitelistData(address collateral) external view returns (bytes memory) {
    return s.transmuterStorage().collaterals[collateral].whitelistData;
  }

  /// @inheritdoc IGetters
  function getStablecoinCap(address collateral) external view returns (uint256) {
    return s.transmuterStorage().collaterals[collateral].stablecoinCap;
  }

  /// @inheritdoc IGetters
  function accessManager() external view returns (address) {
    return address(s.diamondStorage().accessManager);
  }

  /// @inheritdoc IGetters
  function isConsumingScheduledOp() external view returns (bytes4) {
    return s.transmuterStorage().consumingSchedule ? this.isConsumingScheduledOp.selector : bytes4(0);
  }
}

contracts/parallelizer/facets/Redeemer.s.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

import { ITokenP } from "contracts/interfaces/ITokenP.sol";
import { IRedeemer } from "contracts/interfaces/IRedeemer.sol";

import { AccessManagedModifiers } from "./AccessManagedModifiers.sol";
import { LibDiamond } from "../libraries/LibDiamond.sol";
import { LibHelpers } from "../libraries/LibHelpers.sol";
import { LibGetters } from "../libraries/LibGetters.sol";
import { LibManager } from "../libraries/LibManager.sol";
import { LibStorage as s } from "../libraries/LibStorage.sol";
import { LibWhitelist } from "../libraries/LibWhitelist.sol";

import "../../utils/Constants.sol";
import "../../utils/Errors.sol";
import "../Storage.sol";

/// @title Redeemer
/// @author Cooper Labs
/// @custom:contact security@cooperlabs.xyz
/// @dev This contract is an authorized fork of Angle's `Redeemer` contract
/// https://github.com/AngleProtocol/angle-transmuter/blob/main/contracts/parallelizer/facets/Redeemer.sol
contract Redeemer is IRedeemer, AccessManagedModifiers {
  using SafeERC20 for IERC20;
  using Math for uint256;
  using SafeCast for uint256;

  event Redeemed(
    uint256 amount,
    address[] tokens,
    uint256[] amounts,
    address[] forfeitTokens,
    address indexed from,
    address indexed to
  );
  event NormalizerUpdated(uint256 newNormalizerValue);

  /*//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    EXTERNAL ACTIONS                                                 
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////*/

  /// @inheritdoc IRedeemer
  /// @dev The `minAmountOuts` list must reflect the amount of `tokens` returned
  /// @dev In normal conditions, the amount of tokens outputted by this function should be the amount
  /// of collateral assets supported by the system, following their order in the `collateralList`.
  /// @dev If one collateral has its liquidity managed through strategies, then it's possible that this asset
  /// has sub-collaterals with it. In this situation, these sub-collaterals may be sent during the redemption
  /// process and the `minAmountOuts` will be bigger than the `collateralList` length. If there are 3 collateral
  /// assets and the 2nd collateral asset in the list (at index 1) consists of 3 sub-collaterals, then the ordering
  /// of the token list will be as follows:
  /// `[collat 1, sub-collat 1 of collat 2, sub-collat 2 of collat 2, sub-collat 3 of collat 2, collat 3]`
  /// @dev The list of tokens outputted (and hence the minimum length of the `minAmountOuts` list) can be obtained
  /// by calling the `quoteRedemptionCurve` function
  /// @dev Tokens requiring a whitelist must be forfeited if the redemption is to an address that is not in the
  /// whitelist, otherwise this function reverts
  /// @dev No approval is needed before calling this function
  function redeem(
    uint256 amount,
    address receiver,
    uint256 deadline,
    uint256[] memory minAmountOuts
  )
    external
    returns (address[] memory tokens, uint256[] memory amounts)
  {
    return _redeem(amount, receiver, deadline, minAmountOuts, new address[](0));
  }

  /// @inheritdoc IRedeemer
  /// @dev Beware that if a token is given in the `forfeitTokens` list, the redemption will not try to send token
  /// even if it has enough immediately available to send the amount
  function redeemWithForfeit(
    uint256 amount,
    address receiver,
    uint256 deadline,
    uint256[] memory minAmountOuts,
    address[] memory forfeitTokens
  )
    external
    returns (address[] memory tokens, uint256[] memory amounts)
  {
    return _redeem(amount, receiver, deadline, minAmountOuts, forfeitTokens);
  }

  /// @inheritdoc IRedeemer
  /// @dev This function may be called by trusted addresses: these could be for instance savings contract
  /// minting stablecoins when they notice a profit
  function updateNormalizer(uint256 amount, bool increase) external returns (uint256) {
    if (!LibDiamond.checkCanCall(msg.sender, msg.data) && s.transmuterStorage().isTrusted[msg.sender] == 0) {
      revert NotTrusted();
    }
    return _updateNormalizer(amount, increase);
  }

  /// @inheritdoc IRedeemer
  function quoteRedemptionCurve(uint256 amount)
    external
    view
    returns (address[] memory tokens, uint256[] memory amounts)
  {
    (tokens, amounts,) = _quoteRedemptionCurve(amount);
  }

  /*//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    INTERNAL HELPERS                                                 
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////*/

  /// @notice Internal function of the `redeem` function in the `Redeemer` contract
  function _redeem(
    uint256 amount,
    address to,
    uint256 deadline,
    uint256[] memory minAmountOuts,
    address[] memory forfeitTokens
  )
    internal
    nonReentrant
    returns (address[] memory tokens, uint256[] memory amounts)
  {
    ParallelizerStorage storage ts = s.transmuterStorage();

    if (ts.isRedemptionLive == 0) revert Paused();
    if (block.timestamp > deadline) revert TooLate();

    uint256[] memory subCollateralsTracker;
    (tokens, amounts, subCollateralsTracker) = _quoteRedemptionCurve(amount);
    // Check that the provided slippage tokens length is identical to the redeem one
    uint256 amountsLength = amounts.length;
    // If a collateral is added and another one is removed after a redeem transaction is sent, the tokens
    // corresponding to the `minAmountOuts` given may not correspond to the actual collateral `tokens` of
    // the protocol
    if (amountsLength != minAmountOuts.length) revert InvalidLengths();
    // Updating the normalizer enables to simultaneously and proportionally reduce the amount
    // of stablecoins issued from each collateral without having to loop through each of them
    _updateNormalizer(amount, false);

    ITokenP(ts.tokenP).burnSelf(amount, msg.sender);

    address[] memory collateralListMem = ts.collateralList;
    uint256 indexCollateral;
    for (uint256 i; i < amountsLength; ++i) {
      if (amounts[i] < minAmountOuts[i]) revert TooSmallAmountOut();
      // If a token is in the `forfeitTokens` list, then it is not sent as part of the redemption process
      if (amounts[i] > 0 && LibHelpers.checkList(tokens[i], forfeitTokens) < 0) {
        Collateral storage collatInfo = ts.collaterals[collateralListMem[indexCollateral]];
        if (collatInfo.onlyWhitelisted > 0 && !LibWhitelist.checkWhitelist(collatInfo.whitelistData, to)) {
          revert NotWhitelisted();
        }
        if (collatInfo.isManaged > 0) {
          LibManager.release(tokens[i], to, amounts[i], collatInfo.managerData.config);
        } else {
          IERC20(tokens[i]).safeTransfer(to, amounts[i]);
        }
      }
      if (subCollateralsTracker[indexCollateral] - 1 <= i) ++indexCollateral;
    }
    emit Redeemed(amount, tokens, amounts, forfeitTokens, msg.sender, to);
  }

  /// @dev This function reverts if `stablecoinsIssued==0`, which is expected behavior as there is nothing to redeem
  /// anyway in this case, or if the `amountBurnt` is greater than `stablecoinsIssued`
  function _quoteRedemptionCurve(uint256 amountBurnt)
    internal
    view
    returns (address[] memory tokens, uint256[] memory balances, uint256[] memory subCollateralsTracker)
  {
    ParallelizerStorage storage ts = s.transmuterStorage();
    uint64 collatRatio;
    uint256 stablecoinsIssued;
    (collatRatio, stablecoinsIssued, tokens, balances, subCollateralsTracker) = LibGetters.getCollateralRatio();
    if (amountBurnt > stablecoinsIssued) revert TooBigAmountIn();
    int64[] memory yRedemptionCurveMem = ts.yRedemptionCurve;
    uint64 penaltyFactor;
    // If the protocol is under-collateralized, a penalty factor is applied to the returned amount of each asset
    if (collatRatio < BASE_9) {
      uint64[] memory xRedemptionCurveMem = ts.xRedemptionCurve;
      penaltyFactor = uint64(LibHelpers.piecewiseLinear(collatRatio, xRedemptionCurveMem, yRedemptionCurveMem));
    }

    for (uint256 i; i < balances.length; ++i) {
      // The amount given for each token in reserves does not depend on the price of the tokens in reserve:
      // it is a proportion of the balance for each token computed as the ratio between the stablecoins
      // burnt relative to the amount of stablecoins issued.
      // If the protocol is over-collateralized, the amount of each token given is inversely proportional
      // to the collateral ratio.
      balances[i] = collatRatio >= BASE_9
        ? (amountBurnt * balances[i] * (uint64(yRedemptionCurveMem[yRedemptionCurveMem.length - 1])))
          / (stablecoinsIssued * collatRatio)
        : (amountBurnt * balances[i] * penaltyFactor) / (stablecoinsIssued * BASE_9);
    }
  }

  /// @notice Updates the `normalizer` variable used to track stablecoins issued from each asset and globally
  function _updateNormalizer(uint256 amount, bool increase) internal returns (uint256 newNormalizerValue) {
    ParallelizerStorage storage ts = s.transmuterStorage();
    uint256 _normalizer = ts.normalizer;
    uint256 _normalizedStables = ts.normalizedStables;
    // In case of an increase, the update formula used is the simplified version of the formula below:
    /*
            _normalizer * (BASE_27 + BASE_27 * amount / stablecoinsIssued) / BASE_27
        = _normalizer + (_normalizer * BASE_27 * amount * (BASE_27 / (_normalizedStables * normalizer))) / BASE_27
             = _normalizer + BASE_27 * amount / _normalizedStables
        */
    if (increase) {
      newNormalizerValue = _normalizer + (amount * BASE_27) / _normalizedStables;
    } else {
      newNormalizerValue = _normalizer - (amount * BASE_27) / _normalizedStables;
    }
    // If the `normalizer` gets too small or too big, it must be renormalized to later avoid the propagation of
    // rounding errors, as well as overflows. In this case, the function has to iterate through all the
    // supported collateral assets
    if (newNormalizerValue <= BASE_18 || newNormalizerValue >= BASE_36) {
      address[] memory collateralListMem = ts.collateralList;
      uint256 collateralListLength = collateralListMem.length;
      // For each asset, we store the actual amount of stablecoins issued based on the `newNormalizerValue`
      // (and not a normalized value)
      // We ensure to preserve the invariant `sum(collateralNewNormalizedStables) = normalizedStables`
      uint128 newNormalizedStables;
      for (uint256 i; i < collateralListLength; ++i) {
        uint128 newCollateralNormalizedStable = (
          (uint256(ts.collaterals[collateralListMem[i]].normalizedStables) * newNormalizerValue) / BASE_27
        ).toUint128();
        newNormalizedStables += newCollateralNormalizedStable;
        ts.collaterals[collateralListMem[i]].normalizedStables = uint216(newCollateralNormalizedStable);
      }
      ts.normalizedStables = newNormalizedStables;
      newNormalizerValue = BASE_27;
    }
    ts.normalizer = newNormalizerValue.toUint128();
    emit NormalizerUpdated(newNormalizerValue);
  }
}

contracts/parallelizer/facets/RewardHand.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { IRewardHandler } from "contracts/interfaces/IRewardHandler.sol";

import { AccessManagedModifiers } from "./AccessManagedModifiers.sol";

import { LibDiamond } from "../libraries/LibDiamond.sol";
import { LibStorage as s } from "../libraries/LibStorage.sol";
import { LibManager } from "../libraries/LibManager.sol";

import "../../utils/Constants.sol";
import "../../utils/Errors.sol";
import "../Storage.sol";

/// @title RewardHandler
/// @author Cooper Labs
/// @custom:contact security@cooperlabs.xyz
/// @dev This contract is an authorized fork of Angle's `RewardHandler` contract
/// https://github.com/AngleProtocol/angle-transmuter/blob/main/contracts/parallelizer/facets/RewardHandler.sol
contract RewardHandler is IRewardHandler, AccessManagedModifiers {
  using SafeERC20 for IERC20;

  event RewardsSoldFor(address indexed tokenObtained, uint256 balanceUpdate);

  /// @notice IRewardHandler
  /// @dev It is impossible to sell a token that is a collateral through this function
  /// @dev Trusted sellers and governance only may call this function
  /// @dev Only governance can set which tokens can be swapped through this function by passing a prior approval
  /// transaction to Odos router for the token to be swapped
  function sellRewards(uint256 minAmountOut, bytes memory payload) external nonReentrant returns (uint256 amountOut) {
    ParallelizerStorage storage ts = s.transmuterStorage();
    if (!LibDiamond.checkCanCall(msg.sender, msg.data) && ts.isSellerTrusted[msg.sender] == 0) revert NotTrusted();
    address[] memory list = ts.collateralList;
    uint256 listLength = list.length;
    uint256[] memory balances = new uint256[](listLength);
    // Getting the balances of all collateral assets of the protocol to see if those do not decrease during
    // the swap: this is the only way to check that collateral assets have not been sold
    // Not checking the `subCollaterals` here as swaps should try to increase the balance of one collateral
    for (uint256 i; i < listLength; ++i) {
      balances[i] = IERC20(list[i]).balanceOf(address(this));
    }
    //solhint-disable-next-line
    (bool success, bytes memory result) = ODOS_ROUTER.call(payload);
    if (!success) _revertBytes(result);
    amountOut = abi.decode(result, (uint256));
    if (amountOut < minAmountOut) revert TooSmallAmountOut();
    bool hasIncreased;
    address collateral;
    for (uint256 i; i < listLength; ++i) {
      uint256 newBalance = IERC20(list[i]).balanceOf(address(this));
      if (newBalance < balances[i]) {
        revert InvalidSwap();
      } else if (newBalance > balances[i]) {
        hasIncreased = true;
        collateral = list[i];
        emit RewardsSoldFor(list[i], newBalance - balances[i]);
      }
    }
    if (!hasIncreased) revert InvalidSwap();
    Collateral storage collatInfo = s.transmuterStorage().collaterals[collateral];
    if (collatInfo.isManaged > 0) {
      IERC20(collateral).safeTransfer(LibManager.transferRecipient(collatInfo.managerData.config), amountOut);
      LibManager.invest(amountOut, collatInfo.managerData.config);
    }
  }

  /// @notice Processes odos revert messages
  function _revertBytes(bytes memory errMsg) private pure {
    if (errMsg.length > 0) {
      //solhint-disable-next-line
      assembly ("memory-safe") {
        revert(add(32, errMsg), mload(errMsg))
      }
    }
    revert OdosSwapFailed();
  }
}

