k"},"redeem(uint256,address,uint256,uint256[])":{"notice":"Redeems `amount` of stablecoins from the system"},"redeemWithForfeit(uint256,address,uint256,uint256[],address[])":{"notice":"Same as the redeem function above with the additional feature to specify a list of `forfeitTokens` for which the Parallelizer system will not try to do a transfer to `receiver`."},"updateNormalizer(uint256,bool)":{"notice":"Updates the normalizer variable by `amount`"}},"version":1}},"settings":{"compilationTarget":{"contracts/parallelizer/facets/Redeemer.sol":"Redeemer"},"evmVersion":"cancun","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":1000},"remappings":[],"viaIR":true},"sources":{"@openzeppelin/contracts/access/manager/AuthorityUtils.sol":{"content":"// SPDX-License-Identifier: MIT
